import React from "react";
import styled from "styled-components";
import Hello from "../components/Home/Hello";
import AboutMeComponent from "../components/Home/AboutMe";
import ProjectComponent from "../components/Home/Project";
import Storage from "../components/Home/Storage";
import Github from "../components/Home/Github";
import Study from "../components/Home/Study";
import Icon from "../components/Share/Icon";
import { useNavigate } from "react-router-dom";

const Background = styled.div`
  width: 100vw;
  min-height: 100vh;
  background: #e29f91;

  display: flex;
  justify-content: center;
  align-items: center;
`;

const Box = styled.div`
  width: 1440px;
  height: 850px;
  background: #fdfbf8;

  display: flex;
  justify-content: space-between;
  align-items: center;

  border: 1px solid black;
  box-sizing: border-box;
`;

const Left = styled.div`
  width: 200px;
  height: 100%;
  border: 1px solid black;
  box-sizing: border-box;
`;

const Right = styled.div`
  width: 200px;
  height: 100%;
  border: 1px solid black;
  box-sizing: border-box;
`;

const Top = styled.div`
  height: 60px;
  width: 1040px;

  display: flex;
  justify-content: flex-end; /* 오른쪽 정렬 */
  align-items: center; /* 수직 가운데 정렬 */

  border: 1px solid black;
  box-sizing: border-box;
`;

const Content = styled.div`
  height: 715px;
  width: 1040px;
  border: 1px solid black;
  box-sizing: border-box;
`;

const Bottom = styled.div`
  height: 75px;
  width: 1040px;
  border: 1px solid black;
  box-sizing: border-box;
`;

const ItemsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 40px;
`;

const ContentLayout = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 40px;
`;

const ContentLayout2 = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 40px;
  margin-top: 40px;
`;

/** Hover 스타일을 적용할 Styled Component */
const HoverableComponent = styled.div`
  transition: transform 0.3s, background-color 0.3s;
  &:hover {
    transform: scale(1.05); /* 약간 확대 효과 */
    background-color: rgba(226, 159, 145, 0.2); /* 배경색 변경 */
    cursor: pointer; /* 마우스 포인터 변경 */
  }
`;

export default function Home() {
  const navigate = useNavigate();

  const handleNavigate = (path) => {
    navigate(path);
  };

  return (
    <Background>
      <Box>
        <Left></Left>
        <div>
          <Top>
            <Icon></Icon>
          </Top>

          <Content>
            <ContentLayout>
              <ItemsContainer>
                <Hello />
                {/* AboutMe 클릭 및 Hover */}
                <HoverableComponent onClick={() => handleNavigate("/aboutme")}>
                  <AboutMeComponent />
                </HoverableComponent>
              </ItemsContainer>
              {/* Project 클릭 및 Hover */}
              <HoverableComponent onClick={() => handleNavigate("/project")}>
                <ProjectComponent />
              </HoverableComponent>
            </ContentLayout>
            <ContentLayout2>
              <Storage></Storage>
              <Github></Github>
              <Study></Study>
            </ContentLayout2>
          </Content>
          <Bottom></Bottom>
        </div>
        <Right></Right>
      </Box>
    </Background>
  );
}
