import React, { useState } from "react";
import styled from "styled-components";
import closeicon from "../../assets/icon/close.png";
import ReactCalendar from "./Calendar";
import moment from "moment";

const Modal = styled.div`
  position: fixed; /* 화면 고정 */
  top: 50%; /* 화면의 정중앙 */
  left: 50%;
  transform: translate(-50%, -50%); /* 중앙 정렬 */

  width: 500px;
  height: 400px;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 1px solid #4a4e4d;
  background: #ffffff;
  z-index: 1000; /* 배경보다 위로 */
`;

const Left = styled.div`
  width: 50px;
  height: 400px;

  border: 1px solid black;
  box-sizing: border-box;
`;

const Right = styled.div`
  width: 50px;
  height: 400px;

  border: 1px solid black;
  box-sizing: border-box;
`;

const Box = styled.div`
  width: 400px;
  height: 400px;
`;

const TitleText = styled.div`
  height: 35px;

  color: #4a4e4d;
  font-family: Roboto;
  font-size: 30px;
  font-style: normal;
  font-weight: 600;
  line-height: normal;

  margin-top: 30px;
  display: flex; /* Flexbox 사용 */
  justify-content: center; /* 가로 중앙 정렬 */
  align-items: center;

  border: 1px solid black;
`;

const SubText = styled.div`
  color: #4a4e4d;
  font-family: Roboto;
  font-size: 22px;
  font-style: normal;
  font-weight: 600;
  line-height: normal;
  margin-top: 20px;

  border: 1px solid black;
`;

const DateContent = styled.div`
  margin-top: 20px;
  width: 100%;
`;

const Date = styled.div`
  display: flex; /* 가로 배치 */
  justify-content: space-between; /* 왼쪽과 오른쪽에 붙게 정렬 */
  align-items: center; /* 세로 중앙 정렬 */
  margin-top: 10px;
  margin-left: 10px;
  margin-right: 10px;
`;

const DateInput = styled.input`
  height: 30px;
  width: 150px;
  border: 1px solid black;
  border-radius: 10px;

  font-family: Roboto;
  font-size: 18px;
  font-style: normal;
  font-weight: 400;
  line-height: normal;
`;

const CheckboxWrapper = styled.div`
  display: flex;
  align-items: center; /* 텍스트와 체크박스를 세로로 중앙 정렬 */
  justify-content: flex-end; /* 오른쪽 정렬 */
  margin-top: 10px;
  margin-right: 20px;
  width: calc(100% - 20px);
`;

const Checkbox = styled.input.attrs({ type: "checkbox" })`
  width: 18px;
  height: 18px;
  margin-right: 8px; /* 텍스트와의 간격 */
`;

const Content = styled.div`
  margin-top: 10px;
  width: 100%;
  display: flex; /* Flexbox 사용 */
  flex-direction: column; /* 세로 방향 정렬 */
  justify-content: center; /* 가로 중앙 정렬 */
  align-items: center; /* 세로 중앙 정렬 */
`;

const ContentInput = styled.textarea`
  width: 350px;
  height: 80px; /* 높이를 늘려 여러 줄 입력 가능 */
  border-radius: 10px;
  border: 1px solid black;
  outline: none; /* 포커스 시 파란 테두리 제거 */
  resize: none; /* 사용자가 크기 조절하지 못하도록 설정 */
  padding: 10px; /* 내부 여백 추가 */
  font-family: Roboto, sans-serif; /* 텍스트 스타일 */
  font-size: 16px;
  line-height: 1.5;
  box-sizing: border-box;
`;

const CloseIcon = styled.img`
  width: 20px;
  height: 20px;
  cursor: pointer;
  margin-left: 10px;
  margin-top: 10px;
`;

const PlusButton = styled.button`
  height: 35px;
  width: 100px;
  background-color: #fdfbf8;
  border: 1px solid black;
  border-radius: 10px;

  display: block; /* 기본 블록 요소로 설정 */
  margin: 20px auto;
  color: #4a4e4d;
  font-family: Roboto;
  font-size: 22px;
  font-style: normal;
  font-weight: 600;
  line-height: normal;
`;

export default function TimelineModal({ onClose }) {
  const [showCalendar, setShowCalendar] = useState(false);
  const [selectedDate, setSelectedDate] = useState("");

  const handleDateClick = () => {
    setShowCalendar(!showCalendar);
  };

  const handleDateChange = (date) => {
    if (Array.isArray(date) && date.length === 2) {
      const [startDate, endDate] = date;
      const formattedStartDate = moment(startDate).format("YYYY-MM-DD");
      const formattedEndDate = moment(endDate).format("YYYY-MM-DD");

      if (formattedStartDate === formattedEndDate) {
        setSelectedDate(formattedStartDate);
      } else {
        setSelectedDate(`${formattedStartDate} ~ ${formattedEndDate}`);
      }
    } else {
      const formattedDate = moment(date).format("YYYY-MM-DD");
      setSelectedDate(formattedDate);
    }
    setShowCalendar(false);
  };

  return (
    <Modal>
      <Left></Left>
      <Box>
        <TitleText>Timeline 추가</TitleText>
        <DateContent>
          <SubText>기간</SubText>
          <Date>
            <DateInput
              type="text"
              value={selectedDate}
              placeholder="날짜를 선택하세요"
              readOnly
              onClick={handleDateClick}
            />
            {showCalendar && (
              <div style={{ position: "absolute", top: "40px", zIndex: 1000 }}>
                <ReactCalendar onChange={handleDateChange} />
              </div>
            )}
          </Date>
          <CheckboxWrapper>
            <Checkbox />
            진행중
          </CheckboxWrapper>
        </DateContent>
        <SubText>내용</SubText>
        <Content>
          <ContentInput></ContentInput>
        </Content>
        <PlusButton>추가</PlusButton>
      </Box>
      <Right>
        <CloseIcon src={closeicon} onClick={onClose} />
      </Right>
    </Modal>
  );
}
