import React, { useState } from "react";
import styled from "styled-components";
import closeicon from "../../assets/icon/close.png";

const Modal = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 500px;
  height: 400px;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 1px solid #4a4e4d;
  background: #ffffff;
  z-index: 1000;
`;

const DateContent = styled.div`
  margin-top: 20px;
  width: 100%;
`;

const Date = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 10px;
  margin-left: 10px;
  margin-right: 10px;
`;

const DateInput = styled.input`
  height: 30px;
  width: 150px;
  border: 1px solid black;
  border-radius: 10px;
  padding: 10px;
  box-sizing: border-box;
  font-family: Roboto;
  font-size: 18px;
  line-height: normal;
`;

const CheckboxWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  margin-top: 10px;
  margin-right: 20px;
  width: calc(100% - 20px);
`;

const Checkbox = styled.input.attrs({ type: "checkbox" })`
  width: 18px;
  height: 18px;
  margin-right: 8px;
`;

const PlusButton = styled.button`
  height: 35px;
  width: 100px;
  background-color: #fdfbf8;
  border: 1px solid black;
  border-radius: 10px;
  display: block;
  margin: 20px auto;
  color: #4a4e4d;
  font-family: Roboto;
  font-size: 22px;
  font-weight: 600;
`;

export default function TimelineModal({ onClose }) {
  const [isOngoing, setIsOngoing] = useState(false);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const handleCheckboxChange = () => {
    setIsOngoing((prev) => !prev);
    if (!isOngoing) setEndDate(""); // "진행중" 체크 시 끝 날짜 초기화
  };

  const formatDate = (value) => {
    const cleaned = value.replace(/\D/g, ""); // 숫자만 추출
    const year = cleaned.substring(0, 4);
    const month = cleaned.substring(4, 6);
    const day = cleaned.substring(6, 8);

    if (cleaned.length >= 8) {
      return `${year}.${month}.${day}`;
    } else if (cleaned.length >= 6) {
      return `${year}.${month}`;
    } else if (cleaned.length >= 4) {
      return `${year}`;
    }
    return cleaned;
  };

  const handleStartDateBlur = () => {
    setStartDate(formatDate(startDate)); // 포맷 적용
  };

  const handleEndDateBlur = () => {
    setEndDate(formatDate(endDate)); // 포맷 적용
  };

  return (
    <Modal>
      <div>
        <h2>Timeline 추가</h2>
        <DateContent>
          <div>기간</div>
          <Date>
            <DateInput
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)} // 입력 도중에는 포맷팅하지 않음
              onBlur={handleStartDateBlur} // 입력 완료 후 포맷 적용
              placeholder="시작 날짜"
            />
            -
            <DateInput
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)} // 입력 도중에는 포맷팅하지 않음
              onBlur={handleEndDateBlur} // 입력 완료 후 포맷 적용
              placeholder="종료 날짜"
              disabled={isOngoing} // "진행중" 체크 시 비활성화
            />
          </Date>
          <CheckboxWrapper>
            <Checkbox checked={isOngoing} onChange={handleCheckboxChange} />
            진행중
          </CheckboxWrapper>
        </DateContent>
        <PlusButton>추가</PlusButton>
      </div>
      <div>
        <img src={closeicon} alt="Close" onClick={onClose} />
      </div>
    </Modal>
  );
}
